    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
       try {
            // Capturar los datos del formulario
            String idProducto = idproducto.getText();
            String nombreProducto = nombre.getText();
            String descripcionProducto = descripcion.getText();
            double precioProducto = Double.parseDouble(precio.getText());
            String categoriaProducto = idcategoria.getText();
            int stockProducto = Integer.parseInt(stock_disponible.getText());

            // Crear objeto de la capa de l√≥gica de negocio (BL)
            BLproducto productoBL = new BLproducto();
            
            // Llamar a la funci√≥n registrarProducto en la capa de l√≥gica de negocio
            productoBL.agregarProducto(stockProducto, nombreProducto, descripcionProducto, precioProducto, WIDTH, stockProducto);

            JOptionPane.showMessageDialog(this, "Producto registrado con √©xito.");

            // Limpiar campos
            limpiarCampos();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error al registrar el producto: " + e.getMessage());
        }
    }//GEN-LAST:event_btnRegistrarActionPerformed

    private void btnModificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnModificarActionPerformed
       try {
            // Capturar los datos del formulario
            String idProducto = idproducto.getText();
            String nombreProducto = nombre.getText();
            String descripcionProducto = descripcion.getText();
            double precioProducto = Double.parseDouble(precio.getText());
            String categoriaProducto = idcategoria.getText();
            int stockProducto = Integer.parseInt(stock_disponible.getText());

            // Crear objeto de la capa de l√≥gica de negocio (BL)
            BLproducto productoBL = new BLproducto();
            
            // Llamar a la funci√≥n modificarProducto en la capa de l√≥gica de negocio
            productoBL.actualizarProducto(stockProducto, nombreProducto, descripcionProducto, precioProducto, WIDTH, stockProducto);

            JOptionPane.showMessageDialog(this, "Producto modificado con √©xito.");

            // Limpiar campos
            limpiarCampos();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error al modificar el producto: " + e.getMessage());
        }
    }//GEN-LAST:event_btnModificarActionPerformed

    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        try {
            // Capturar el ID del producto para eliminar
            String idProducto = idproducto.getText();

            // Crear objeto de la capa de l√≥gica de negocio (BL)
            BLproducto productoBL = new BLproducto();
            
            // Llamar a la funci√≥n eliminarProducto en la capa de l√≥gica de negocio
            productoBL.eliminarProducto(WIDTH);

            JOptionPane.showMessageDialog(this, "Producto eliminado con √©xito.");

            // Limpiar campos
            limpiarCampos();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error al eliminar el producto: " + e.getMessage());
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

     private void limpiarCampos() {
        idproducto.setText("");
        nombre.setText("");
        descripcion.setText("");
        precio.setText("");
        idcategoria.setText("");
        stock_disponible.setText("");
    }
